import React from "react";
import ReactDOM from "react-dom";

import "./index.css";
let today = new Date();

let time = today.getHours();

let greet = "";
if (time >= 1 && time < 12) {
  greet = "Good Morning";
  greet.style.color = "green";
} else if (time >= 12 && time < 18) {
  greet = "Good AfterNoon";

} else {
  greet = "Good Evening";
  greet.style.color = "green";
}

ReactDOM.render(
  <>
    <h1>Hello Sir/Madam,{greet}</h1>
  </>,
  document.getElementById("root")
);
